using BurgerLink.Order.Contracts.Commands;using BurgerLink.Order.Entity;using BurgerLink.Order.Services;using MassTransit;namespace BurgerLink.Order.Consumers.StartOrder;public class CreateOrderConsumer : IConsumer<CreateOrder>{    private readonly IOrderService _orderService;    public CreateOrderConsumer(IOrderService orderService)    {        _orderService = orderService ?? throw new ArgumentNullException(nameof(orderService));    }    public async Task Consume(ConsumeContext<CreateOrder> context)    {        var order = new OrderEntity        {            OrderName = context.Message.OrderName,            Ingredients = new List<string>(),            Validating = false,            StatusUpdateAddress = context.Message.StatusUpdateAddress        };        await _orderService.Collection.InsertOneAsync(order);    }}